import React, { useState } from 'react';
import './App.scss';
import $ from "jquery";




function App() 
{
  // variables
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");
  const [result, setResult] = useState("");

  //functions
  const handleNameChange = (ev) =>
  {
    setName(ev.target.value);
  }
  const handlePasswordChange = (ev) =>
  {
    setPassword(ev.target.value); 
  }

  const handleSubmit = (ev) =>
  {
    ev.preventDefault();

    const form = $(ev.target);
    $.ajax({
      type: "POST",
      url: form.attr("action"),
      data: form.serialize(),
      success(data){
        setResult(data);
      }
    });

  }

  return(
    <>
      <h1>
        Documents
      </h1>

      <div className="login_form">
        <form action='http://localhost:8000' method='post' onSubmit={(event) => handleSubmit(event)}>
          <input type='text' id='name' name='name' placeholder='name' onChange={(event) => handleNameChange(event)}/>
          <br/>
          <input type='password' id='name' name='name' placeholder='password' onChange={(event) => handlePasswordChange(event)}/>
          <br/>
          <button type='submit'>Submit!</button>
        </form>


      </div>
    </>
  );
}

export default App;
